script:
name = frappe.form_dict['name']

item_status = frappe.db.sql(" select status from `tabGate Pass Item` where parent=%s ",(name), as_dict=True)

status_check = "Not"
for item in item_status:
    status = item.get('status')  
    if status == "Not Return":
        status_check = "Yes"
        break

if status_check == "Not":
    frappe.db.set_value("Gate Pass", str(name), "docstatus", 3)
